import {useState,useEffect} from 'react';
import axios from "axios";
import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import classes from './AvailableMeals.module.css';

const AvailableMeals = () => {

  const [dum_meal,setMealdata]=useState([]);

  useEffect(() =>{
    try {
        axios.get('https://api-project-391044883508.firebaseio.com/Meals.json')
        .then(res => {
          for(var i in  res.data) {
            setMealdata((data) => [...data,{id: i , name : res.data[i].name, description : res.data[i].description, price : res.data[i].price}]);    
          }

        });  
    }catch(error){
      throw new Error(error);
    }

   },[]);


  const mealsList = dum_meal.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
